import {useReducer} from 'react';
import TaskContext from './taskContext';
import TaskReducer from './taskReducer';
import {GET_TASKS,
        ADD_TASK,
        VALIDATE_TASK,
        DELETE_TASK
    } from   '../../types';

const TaskState = props => {

    const initialState = {
        tasks: [
            {name: 'Elegir plataforma', status: true, proyectid: 1 },
            {name: 'Elegir color', status: false, proyectid: 2 },
            {name: 'Elegir pago', status: false, proyectid: 3 },
            {name: 'Elegir hosting', status: true, proyectid: 4 },
            {name: 'Elegir plataforma', status: true, proyectid: 3 },
            {name: 'Elegir color', status: false, proyectid: 1 },
            {name: 'Elegir pago', status: false, proyectid: 4 },
            {name: 'Elegir hosting', status: true, proyectid: 2 },
        ],
        taskproyect: null,
        taskerror: false
    }

    //Dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(TaskReducer, initialState);

    //Actions
    const getTask = proyectid => {
        dispatch({
            type: GET_TASKS,
            payload: proyectid
        })
    }

    const addTask = task => {
        dispatch({
            type: ADD_TASK,
            payload: task
        })
    }

    const validateTask = () => {
        dispatch({
            type: VALIDATE_TASK
        })
    }

    return (
        <TaskContext.Provider
            value={{
                tasks: state.tasks,
                getTask,
                taskproyect: state.taskproyect,
                addTask,
                taskerror: state.taskerror,
                validateTask
            }}>
            {props.children}
        </TaskContext.Provider>
    )
}


export default TaskState;
