import {GET_TASKS, 
        ADD_TASK,
        VALIDATE_TASK,
        DELETE_TASK,
        STATUS_TASK
        } from   '../../types';

//Aque tenemos las condiciones de nuestro State
export default (state, action) => {
    switch (action.type) {
        case GET_TASKS:
            return {
                ...state,
                taskproyect: state.tasks.filter(task => task.proyectid === action.payload)
            }
        case ADD_TASK:
            return {
                ...state,
                tasks: [action.payload, ...state.tasks],
                taskerror: false
            }
        case VALIDATE_TASK:
            return {
                ...state,
                taskerror: true
            }
        case DELETE_TASK:
            return {
                ...state,
                tasks: state.tasks.filter(task => task.taskid !== action.payload)
            }
        case STATUS_TASK:
            return {
                ...state,
                tasks: state.tasks.map(task => task.taskid === action.payload.id ? action.payload : task)
            }
        default: 
            return state;
    }
}
