import { FORM_PROYECT, 
    GET_PROYECTS,
    ADD_PROYECT,
    SHOW_ERROR
    } from '../../types/index';

//Aque tenemos las condiciones de nuestro State
export default (state, action) => {
    switch (action.type) {
        case FORM_PROYECT:
            return {
                ...state,
                form: true
            }
        case GET_PROYECTS:
            return {
                ...state,
                proyects: action.payload
            }
        case ADD_PROYECT:
            return {
                ...state,
                proyects: [...state.proyects, action.payload],
                form: false,
                errorForm: false
            }
        case SHOW_ERROR:
            return {
                ...state,
                errorform: true
            }

        default: 
            return state;
    }
}
