import {useReducer} from 'react';
import proyectContext from './proyectContext';
import proyectReducer from './proyectReducer';
import {FORM_PROYECT, 
    GET_PROYECTS,
    ADD_PROYECT,
    SHOW_ERROR,
    ACTUAL_PROYECT,
    DELETE_PROYECT
        } from '../../types/index';
import { v4 as uuidv4 } from 'uuid';



const ProyectState = props => {
    const proyects = [
        {id: 1, proyectName: 'Tienda Virtual'},
        {id: 2, proyectName: 'Intranet'},
        {id: 3, proyectName: 'Diseño de páginas web'},
        {id: 4, proyectName: 'Diseño de caras'}
    ]

    const initialState = {
        form: false,
        proyects: [],
        errorform: false,
        proyect: null
    }
    //dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(proyectReducer, initialState);

    //funciones para el CRUD
    const mostrarForm = () => {
        dispatch({
            type: FORM_PROYECT
        })
    };

    const getProyects = () => {
        dispatch({
            type: GET_PROYECTS,
            payload: proyects
        })
    };

    const addProyect = proyect => {
        proyect.id = uuidv4();
        dispatch({
            type: ADD_PROYECT,
            payload: proyect
        })
    }

    const showError = () => {
        dispatch({
            type: SHOW_ERROR
        })
    }

    const selectProyect = proyect => {
        dispatch({
            type: ACTUAL_PROYECT,
            payload: proyect
        })
    }

    const deleteProyect = proyectid => {
        dispatch({
            type: DELETE_PROYECT,
            payload: id
        })
    }

    return (
        <proyectContext.Provider 
            value={{
                form: state.form,
                mostrarForm,
                proyects: state.proyects,
                getProyects,
                addProyect,
                errorform: state.errorform,
                showError,
                proyect: state.proyect,
                selectProyect
            }}
        >
            {props.children}
        </proyectContext.Provider>
    )


};

export default ProyectState;
