import { useContext, useState } from 'react';
import proyectContext from '../../context/Proyectos/proyectContext'
import taskContext from '../../context/Tasks/taskContext';

const FormTask = () => {

    const proyectsContext = useContext(proyectContext);
    const { proyect } = proyectsContext;

    const tasksContext = useContext(taskContext);
    const {addTask, validateTask, taskerror} = tasksContext;
    const [task, setTask] = useState({
        name: ''
    });
    const { name } = task;

    if(!proyect) return null;

    const [actualProyect] = proyect;

    const handleChange = (e) => {
        setTask({
            ...task,
            [e.target.name]: e.target.value
        })
    }

    const onSubmit = (e) => {
        e.preventDefault();
        //validar
        if(name.trim() === '') {
            validateTask();
            return;
        }
        //pasarlo al context
        //Agregar la tarea al state de tareas
        task.proyectid = actualProyect.id;
        task.status = false;
        addTask(task);
        //reiniciar el form
        
    }

    return ( 
        <div className="formulario">
            <form onSubmit={onSubmit}>
                <div className="contenedor-input">
                    <input
                    value={name}
                    onChange={handleChange}
                    className="input-text"
                    type="text" 
                    placeholder="Nombre de la tarea"
                    name="name"  
                    />
                </div>
                <div className="contenedor-input">
                    <input
                    className="btn btn-primario btn-submit btn-block"
                    type="submit"
                    value="Agregar Tarea" 
                    />
                </div>
            </form>
            {taskerror ? <p className="mensaje error">El nombre de la tarea es obligatorio</p> : null}
        </div>
     );
}
 
export default FormTask;
