import { Fragment, useState, useContext } from "react";
import proyectContext from '../../context/Proyectos/proyectContext'

const NewProyect = () => {

    //State para nuevo proyecto
    const proyectsContext = useContext(proyectContext);
    const {form, mostrarForm, addProyect} = proyectsContext;

    const [proyect, setProyect] = useState({
        proyectName: "",
    });

    const { proyectName } = proyect;
    
    const handleChange = (e) => {
        setProyect({
            ...proyect,
            [e.target.name]: e.target.value
        })
    };

    const onSubmitProyect = (e) => {
        e.preventDefault();
        //validar el proyecto
        if(proyectName === '') {
            return;
        }
        //agregar al state
        addProyect(proyect);
        //reiniciar el form
    };

    return ( 
        <Fragment>
        <button 
            onClick={mostrarForm}
            type="button"
            className="btn btn-block btn-primario"
            >
            Nuevo Proyecto
        </button>
        { form ?
            (<form onSubmit={onSubmitProyect} className="formulario-nuevo-proyecto">
                <input 
                    onChange={handleChange}
                    value={proyectName}
                    type="text"
                    className="input-text"
                    placeholder="Nombre del Proyecto"
                    name="proyectName"
                />
                <input 
                    type="submit"
                    className="btn btn-block btn-primario"
                    value="Agregar Proyecto"
                />
            </form>)
        : null}
        </Fragment>
     );
}
 
export default NewProyect;
