import { useContext, useEffect } from 'react';
import proyectContext from '../../context/Proyectos/proyectContext';


const ListProyects = () => {
    const proyectsContext = useContext(proyectContext);
    const {proyects, getProyects, selectProyect} = proyectsContext;

    useEffect(() => {
        getProyects();
    }, []);
    //los useeffect se ejecutan cuando el componente se monta y cuando se desmonta
    //los condicionales siempre abajo de todo
    if(proyects.length === 0) {
        return <p className="mensaje">No tienes Proyectos, crea todos los que necesites!</p>;
    }

    const proyectoSeleccionado = id => {
        selectProyect(id);
    }

    return ( 
        <ul className="listado-proyectos">
            {proyects.map(proyect => (
                <li key={proyect.id}>
                <button
                    onClick={ () => proyectoSeleccionado(proyect.id) }
                    type="button"
                    className="btn btn-black"
                >{proyect.proyectName}</button>
                </li>
            ))}
        </ul>
     );
}
 
export default ListProyects;
