import { Fragment, useContext } from "react";
import Task from '../tareas/Task';
import proyectContext from '../../context/Proyectos/proyectContext'


const ListTask = () => {
    const proyectsContext = useContext(proyectContext);
    const { proyect } = proyectsContext;

    const [actualProyect] = proyect; //posicion uno del array

    const proyectTasks = [
        {name: 'Elegir plataforma', status: true },
        {name: 'Elegir color', status: false },
        {name: 'Elegir pago', status: false },
        {name: 'Elegir hosting', status: true },
    ];

    return (
        <Fragment>
        <h2>Proyecto: {actualProyect.proyectName}</h2>
        <ul className="listado-tareas">
            {proyectTasks.length === 0 
            ? (<li className="tarea"><p>No hay tareas</p></li>)
            : proyectTasks.map(task => (
                <Task key={task.name} task={task} />
            ))}
        </ul>

        <button
            type="button"
            className="btn btn-eliminar"
        >Eliminar Proyecto &times;</button>
        </Fragment>
     );
}
 
export default ListTask;
