import {GET_TASKS, 
    ADD_TASK,
    VALIDATE_TASK
    } from   '../../types';

//Aque tenemos las condiciones de nuestro State
export default (state, action) => {
    switch (action.type) {
        case GET_TASKS:
            return {
                ...state,
                taskproyect: state.tasks.filter(task => task.proyectid === action.payload)
            }
        case ADD_TASK:
            return {
                ...state,
                tasks: [...state.tasks, action.payload]
            }
        default: 
            return state;
    }
}
