import { v4 as uuidv4 } from 'uuid';
import {useReducer} from 'react';
import TaskContext from './taskContext';
import TaskReducer from './taskReducer';
import {GET_TASKS,
        ADD_TASK,
        VALIDATE_TASK,
        DELETE_TASK,
        STATUS_TASK,
        ACTUAL_TASK,
        UPDATE_TASK,
    } from   '../../types';

const TaskState = props => {

    const initialState = {
        tasks: [
            { id: 1, name: 'Elegir plataforma', status: true, proyectid: 1 },
            { id: 2, name: 'Elegir color', status: false, proyectid: 2 },
            { id: 3, name: 'Elegir pago', status: false, proyectid: 3 },
            { id: 4, name: 'Elegir hosting', status: true, proyectid: 4 },
            { id: 5, name: 'Elegir plataforma', status: true, proyectid: 3 },
            { id: 6, name: 'Elegir color', status: false, proyectid: 1 },
            { id: 7, name: 'Elegir pago', status: false, proyectid: 4 },
            { id: 8, name: 'Elegir hosting', status: true, proyectid: 2 },
        ],
        taskproyect: null,
        taskerror: false,
        selectedtask: null
    }

    //Dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(TaskReducer, initialState);

    //Actions
    const getTask = proyectid => {
        dispatch({
            type: GET_TASKS,
            payload: proyectid
        })
    }

    const addTask = task => {
        task.taskid = uuidv4();
        dispatch({
            type: ADD_TASK,
            payload: task
        })
    }

    const validateTask = () => {
        dispatch({
            type: VALIDATE_TASK
        })
    }

    const deleteTask = id => {
        dispatch({
            type: DELETE_TASK,
            payload: id
        })
    } 

    const statustask = task => {
        dispatch({
            type: STATUS_TASK,
            payload: task
        })
    }

    const actualtask = task => {
        dispatch({
            type: ACTUAL_TASK,
            payload: task
        })
    }

    const updateTask = task => {
        dispatch({
            type: UPDATE_TASK,
            payload: task
        })
    }

    return (
        <TaskContext.Provider
            value={{
                tasks: state.tasks,
                getTask,
                taskproyect: state.taskproyect,
                addTask,
                taskerror: state.taskerror,
                validateTask,
                deleteTask,
                statustask,
                actualtask,
                selectedtask: state.selectedtask,
                updateTask
            }}>
            {props.children}
        </TaskContext.Provider>
    )
}


export default TaskState;
