import { useContext } from 'react';
import taskContext from '../../context/Tasks/taskContext';
import proyectContext from '../../context/Proyectos/proyectContext'


const Task = ({task}) => {
    const proyectsContext = useContext(proyectContext);
    const { proyect } = proyectsContext;
    const [actualProyect] = proyect;

    const tasksContext = useContext(taskContext);
    const {deleteTask, getTask} = tasksContext;

    const taskDelete = taskid => {
        deleteTask(taskid);
        getTask(actualProyect.proyectid);
    }

    return ( 
        <li className="tarea sombra">
            <p>{task.name}</p>
            <div className="estado">
                {task.status
                ? (<button type="button" className="completo">Completado</button>)
                : (<button type="button" className="incomleto">Incompleto</button>) }
            </div>
            <div className="acciones">
                    <button type="button" className="btn btn-primario">Editar</button>
                    <button onClick={() => taskDelete(task.taskid)} type="button" className="btn btn-secundario">Eliminar</button>
            </div>
        </li>
     );
}
 
export default Task;
