import {GET_TASKS, ADD_TASK
    } from   '../../types';

//Aque tenemos las condiciones de nuestro State
export default (state, action) => {
    switch (action.type) {
        case GET_TASKS:
            return {
                ...state,
                taskproyect: state.tasks.filter(task => task.proyectid === action.payload)
            }
        default: 
            return state;
    }
}
