import {useReducer} from 'react';
import proyectContext from './proyectContext';
import proyectReducer from './proyectReducer';
import {FORM_PROYECT, GET_PROYECTS} from '../../types/index';


const proyects = [
    {id: 1, name: 'Tienda Virtual'},
    {id: 2, name: 'Intranet'},
    {id: 3, name: 'Diseño de páginas web'},
    {id: 4, name: 'Diseño de caras'}
]

const ProyectState = props => {
    const initialState = {
        form: false,
        proyects: []
    }
    //dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(proyectReducer, initialState);

    //funciones para el CRUD
    const mostrarForm = () => {
        dispatch({
            type: FORM_PROYECT
        })
    };

    const getProyects = (proyects) => {
        dispatch({
            type: GET_PROYECTS,
            payload: proyects
        })
    }

    return (
        <proyectContext.Provider 
            value={{
                form: state.form,
                mostrarForm,
                proyects: state.proyects
            }}
        >
            {props.children}
        </proyectContext.Provider>
    )


};

export default ProyectState;
