import {useReducer} from 'react';
import proyectContext from './proyectContext';
import proyectReducer from './proyectReducer';
import {FORM_PROYECT, 
    GET_PROYECTS,
    ADD_PROYECT
        } from '../../types/index';
import uuid from 'uuid';



const ProyectState = props => {
    const proyects = [
        {id: 1, name: 'Tienda Virtual'},
        {id: 2, name: 'Intranet'},
        {id: 3, name: 'Diseño de páginas web'},
        {id: 4, name: 'Diseño de caras'}
    ]

    const initialState = {
        form: false,
        proyects: []
    }
    //dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(proyectReducer, initialState);

    //funciones para el CRUD
    const mostrarForm = () => {
        dispatch({
            type: FORM_PROYECT
        })
    };

    const getProyects = () => {
        dispatch({
            type: GET_PROYECTS,
            payload: proyects
        })
    };

    const addProyect = proyect => {
        proyect.id = uuid.v4();
        dispatch({
            type: ADD_PROYECT,
            payload: proyect
        })
    }

    return (
        <proyectContext.Provider 
            value={{
                form: state.form,
                mostrarForm,
                proyects: state.proyects,
                getProyects
            }}
        >
            {props.children}
        </proyectContext.Provider>
    )


};

export default ProyectState;
