import { 
    REGISTRO_EXITOSO,
    REGISTRO_ERROR,
    OBTENER_USUARIO,
    LOGIN_EXITOSO,
    LOGIN_ERROR,
    CERRAR_SESION
} from '../../types';


export default (state, action) => {
switch (action.type) {
    case LOGIN_EXITOSO:
    case REGISTRO_EXITOSO:
        localStorage.setItem('token', action.payload.token);
        return {
            ...state,
            authenticated: true,
            message: null
        }
    case OBTENER_USUARIO:
        return {
            ...state,
            user: action.payload
        }
    case LOGIN_ERROR:
    case REGISTRO_ERROR:
        localStorage.removeItem('token');
        return {
            ...state,
            token: null,
            message: action.payload,
        }
    case CERRAR_SESION:
        localStorage.removeItem('token');
        return {
            ...state,
            token: null,
            authenticated: false,
            user: null
        }
    default: 
        return state;
}
}
